sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    var cu = new ClaimsUtils();
	var isEmail = cu.validateEmailAddress(current.u_requested_for_email);

    if(isEmail == false){
        gs.addErrorMessage(current.u_requested_for_email + " is not a valid email address.  You must provide a valid email address.");
        current.setAbortAction(true);
    }
})(current, previous);
