sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var ga = new GlideAjax('ClaimsUtilsAjax');
 ga.addParam('sysparm_name', 'validateEmailAddress');
 ga.addParam('sysparm_mail', newValue);

	ga.getXMLAnswer(function(response){

	if (response == false) {

		g_form.showErrorBox('u_requested_for_email', g_form.getValue('u_requested_for_email')+' is not a valid email address.  You must provide a valid email address.', true);

		return false;

	}
});
}
