sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

    var emailStr = g_form.getValue('u_requested_for_email');
    if(!emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
        g_form.showErrorBox('u_requested_for_email', g_form.getValue('u_requested_for_email')+' is not a valid email address.  You must provide a valid email address.', true);
		//return false
	} else {
		g_form.hideErrorBox('u_requested_for_email');
		//return true
	}




}
