var ClaimsUtilsAjax = Class.create();
ClaimsUtilsAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getEmailAdress: function() {
var userSysId = this.getParameter('sysparm_user');

var user = new GlideRecord('sys_user');
if(user.get(userSysId)){
return user.getValue('email');
}},

getWhenNeededDate: function() {
var theDate = new GlideDateTime();

    theDate.addDaysUTC(30);

	return theDate.getLocalDate();
},

